import {
  Card,
  AppBar,
  Container,
  CardHeader,
  CardActions,
  CardMedia,
  CardContent,
  Typography,
  Toolbar,
  Box,
  IconButton,
  Button,
  Avatar,
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ThumbUpIcon from '@material-ui/icons/ThumbUp';
import { red } from "@material-ui/core/colors";
import { makeStyles } from "@material-ui/core/styles";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import { useState } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 700,
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
  container: {
    marginTop: "100px",
  },
}));
export default function Post() {
  const classes = useStyles();
  return (
    <>
      <AppBar postion="relative" style={{ background: "#2E3B55" }}>
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <IconButton color="inherit">
            <Typography color="inherit" variant="h5">
              Posts
            </Typography>
          </IconButton>
        </Toolbar>
      </AppBar>

      <Container maxWidth="sm" className={classes.container}>
        <Card className={classes.root}>
          <CardHeader
            avatar={
              <Avatar variant="circular" className={classes.avatar}>
                H
              </Avatar>
            }
            action={
              <IconButton aria-label="settings">
                <MoreVertIcon />
              </IconButton>
            }
            title="Is Shrek real ? If so, would he make porn ?"
            subheader={new Date().toLocaleDateString("en-US")}
          />
          <CardContent>
            <Typography variant="body2" color="textSecondary" component="p">
              Shrek is love, Shrek is life. If you oppose, you are gei. It is
              all orge now. Shrekporn is the most important thing to human life
            </Typography>
          </CardContent>
          <CardActions>
            <IconButton aria-label="add to favorites">
              <FavoriteIcon />
            </IconButton>

          </CardActions>
        </Card>
      </Container>
    </>
  );
}
