import React, { useState } from "react";
import {
  AppBar,
  Typography,
  Card,
  Paper,
  CardContent,
  TextField,
  Button,
  CardHeader,
  Container,
  FormControl,
  InputLabel,
  Input,
  FormHelperText,
  Divider,
} from "@material-ui/core";
import { blue, cyan, red } from "@material-ui/core/colors";
import data from "../MOCK_DATA";
import { makeStyles } from "@material-ui/core/styles";
const useStyles = makeStyles((theme) => ({
  root: {
    position: "relative",
    marginTop: theme.spacing(2),
    padding: `${theme.spacing(2)}px ${theme.spacing(1)}px`,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    background:
      "linear-gradient(0deg, rgba(198,35,184,1) 10%, rgba(29,57,199,1) 92%)",
    height: 500,
    width: 500,
    borderRadius: "12px",
  },
  labels: {
    fontWeight: 300,
    fontSize: "23px",
  },
  focused: {
    borderColor: red[300],
    borderRadius: "8px",
    boxShadow: "1px 2px 20px rgba(169,198,217,0.29457423) ",
    background:
      "linear-gradient(90deg, rgba(252,70,107,1) 17%, rgba(63,94,251,1) 93%)",
  },
  focusedLabel: {
    fontSize: "18px",
    color: `${blue[300]} !important`,
  },
  email: {
    fontSize: "14px",
    marginTop: "100px",
  },
  password: {
    fontSize: "14px",
    marginTop: "40px",
  },
  button: {
    fontSize: "14px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginTop: "40px",
  },
  notchedOutline: {
    borderColor: "rgba(206,212,218, .993)",
    borderRadius: "8px",
    boxShadow: "1px 2px 20px rgba(169,198,217,0.29457423) ",
  },
  insideButton: {
    width : "120px",
    height : "50px",
    boxShadow: "none",
    textTransform: "none",
    fontSize: 16,
    padding: "6px 12px",
    border: "1px solid",
    lineHeight: 1.5,
    background: blue[600],
    borderColor:  "rgba(206,212,218, .993)",
    borderRadius: 8,
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(","),
    "&:hover": {
      background: "linear-gradient(90deg, rgba(252,70,107,1) 17%, rgba(63,94,251,1) 93%)",
      borderColor: "rgba(206,212,218, .993)",
      boxShadow: "none",
    },
    "&:active": {
      boxShadow: "none",
      backgroundColor: "#0062cc",
      borderColor: "rgba(206,212,218, .993)",
    },
    "&:focus": {
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.5)",
    },
  },
}));
function Login() {
  const classes = useStyles();
  return (
    <Paper elevation={24} className={classes.root}>
      <form>
        <TextField
          fullWidth
          variant="outlined"
          className={classes.email}
          label="Email"
          InputLabelProps={{
            classes: {
              root: classes.labels,
              focused: classes.focusedLabel,
            },
          }}
          InputProps={{
            classes: {
              notchedOutline: classes.notchedOutline,
              focused: classes.focused,
            },
          }}
        ></TextField>

        <TextField
          fullWidth
          variant="outlined"
          className={classes.password}
          label="Password"
          InputLabelProps={{
            classes: {
              root: classes.labels,
              focused: classes.focusedLabel,
            },
          }}
          InputProps={{
            classes: {
              notchedOutline: classes.notchedOutline,
              focused: classes.focused,
            },
          }}
        ></TextField>
        <div className={classes.button}>
          <Button type="submit" variant="contained" classes={
              {
                  root: classes.insideButton
              }
          }>
            Login
          </Button>
          <FormHelperText>Don't have an account</FormHelperText>
        </div>
      </form>
    </Paper>
  );
}

export default Login;
