import {
  Card,
  CardHeader,
  CardActions,
  CardContent,
  Typography,
  IconButton,
  Avatar,
  Paper,
  Collapse,
} from "@material-ui/core";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ThumbUpIcon from "@material-ui/icons/ThumbUp";
import { red, blue } from "@material-ui/core/colors";
import { makeStyles } from "@material-ui/core/styles";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import { useState } from "react";
import Comment from "./Comment";
const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "auto",
    marginTop: "100px",
    backgroundColor: blue[200],
    marginLeft: "auto",
    marginRight: "auto",
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
}));
export default function SinglePost() {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  return (
    <Paper elevation="24"> 
      <Card className={classes.root} square = "false">
        <CardHeader
          avatar={
            <Avatar variant="circular" className={classes.avatar}>
              H
            </Avatar>
          }
          action={
            <IconButton aria-label="settings">
              <MoreVertIcon />
            </IconButton>
          }
          title="Is Shrek real ? If so, would he make porn ?"
          subheader={new Date().toLocaleDateString("en-US")}
        />
        <CardContent>
          <Typography variant="h5" color="textSecondary" component="p">
            Shrek is love, Shrek is life. If you oppose, you are gei. It is all
            orge now. Shrekporn is the most important thing to human life
          </Typography>
        </CardContent>
        <CardActions>
          <IconButton aria-label="add to favorites">
            <Typography>120</Typography>
            <FavoriteIcon />
          </IconButton>
          <IconButton aria-label="add to favorites">
            <Typography>11</Typography>
            <ThumbUpIcon />
          </IconButton>
          <IconButton onClick={() => setOpen(!open)}>
            <Typography>Comment</Typography>
          </IconButton>
        </CardActions>
        <Collapse in={open}>
          <Comment />
        </Collapse>
      </Card>
    </Paper>
  );
}
