import {
  Card,
  AppBar,
  Container,
  CardActions,
  CardMedia,
  Typography,
  Toolbar,
  Box,
  IconButton,
  Button,
  Avatar
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu"
import {red} from "@material-ui/core/colors"
import { makeStyles } from '@material-ui/core/styles';
import { useState } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 500,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));
export default function Post() {
  const classes = useStyles();
  return (
    <>
      <AppBar postion = "static" style={{ background: '#2E3B55' }}>
            <Toolbar>
                <IconButton edge = "start" color = "inherit" aria-label="menu"><MenuIcon/></IconButton>
                <IconButton color = "inherit">
                  <Typography color = "inherit" variant = "h5">Posts</Typography>
                </IconButton>
            </Toolbar>
      </AppBar>
      <Container maxWidth="sm">
          <Card className={classes.root}>
            <Avatar variant = "circle" className={classes.avatar}>Hieu</Avatar>
          </Card>
      </Container>
    </>  
  );
}
