import { Container, Divider, makeStyles, Collapse } from "@material-ui/core";
import { useState, useEffect } from "react";
import { blue } from "@material-ui/core/colors";
import SinglePost from "./SinglePost";
import MakePost from "./MakePost";
import api from "./Api";
import Grow from "@material-ui/core/Grow";
import Pagination from "@material-ui/lab/Pagination";
import { TransitionGroup } from "react-transition-group";

const useStyles = makeStyles((theme) => ({
  ul: {
    "& .MuiPaginationItem-root": {
      boxShadow: "none",
      textTransform: "none",
      fontSize: 13,
      padding: "6px 12px",

      lineHeight: 1.5,
      backgroundColor: blue[600],
      borderColor: "#0063cc",
      color: "#fff",
      "&:hover": {
        backgroundColor: "#0069d9",
        borderColor: "#0062cc",
        boxShadow: "none",
      },
      "&:active": {
        boxShadow: "none",
        backgroundColor: "#0062cc",
        borderColor: "#005cbf",
      },
      "&:focus": {
        boxShadow: "0 0 0 0.2rem rgba(0,123,255,.5)",
      },
    },
  },
}));

export default function Post() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState();
  const [totalPage, setTotalPage] = useState();
  const [currentPage, setCurrentPage] = useState(1);
  const classes = useStyles();
  console.log(currentPage);
  useEffect(() => {
    async function createPost() {
      if (post) {
        try {
          const body = { content: post.postContent, title: post.postTitle };
          await api.post("/posts", body, {
            headers: {
              Authorization: localStorage.getItem("jwt"),
            },
          });
        } catch (err) {
          console.log(err);
        }
      }
    }
    async function fetchPost() {
      const response = await api.get(`/posts/?page=${currentPage}&limit=10`, {
        headers: {
          Authorization: localStorage.getItem("jwt"),
        },
      });
      const { posts, numPosts } = response.data;
      return { posts, numPosts };
    }
    createPost();
    fetchPost().then((data) => {
      setPosts(data.posts);
      setTotalPage(data.numPosts);
    });
  }, [post, currentPage]);
  return (
    <>
      <Container maxWidth="md">
        <MakePost setPost={setPost} />

          {posts?.map((post, index) => {
            return (
              <Collapse timeout={2000}>
                <SinglePost key={post._id} post={post} />
              </Collapse>
            );
          })}

        <Pagination
          count={
            totalPage % 10 === 0
              ? totalPage / 10
              : Math.round(totalPage / 10) + 1
          }
          classes={{ ul: classes.ul }}
          size="large"
          style={{ marginTop: "30px" }}
          onChange={(e) => setCurrentPage(e.target.innerText)}
        />
      </Container>
    </>
  );
}
